import { Ionicons } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';
import { useRouter } from 'expo-router';
import React, { useCallback } from 'react';
import {
  FlatList,
  Image,
  SafeAreaView,
  Text,
  TouchableOpacity,
  View,
} from 'react-native';

export default function Homescreen() {
  const router = useRouter();
  const navigation = useNavigation();

  const featuredBooks = [
    {
      id: '1',
      desc:" Lorem ipsum dolor, sit amet consectetur adipisicing elit. Ex hic vel perspiciatis cum, debitis nostrum voluptas alias totam incidunt, deleniti temporibus magni nisi non necessitatibus numquam, ducimus pariatur earum quisquam.",
      cover:
        'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQlIokz68gTar-Q_-91nMeg37zpcHUnEem28Q&sdata:image/jpeg;base64,',
      title: 'The Trial',
      author: 'Frank Kafka',
    },
    {
      id: '2',
      desc:" Lorem ipsum dolor, sit amet consectetur adipisicing elit. Ex hic vel perspiciatis cum, debitis nostrum voluptas alias totam incidunt, deleniti temporibus magni nisi non necessitatibus numquam, ducimus pariatur earum quisquam.",
      cover:
        'data:image/jpeg;base64,',
      title: 'Metamorphosis',
      author: 'Frank Kafka',
    },
    {
      id: '3',
      cover:
        'data:image/jpeg;base64,',
      title: 'Crime and punishment',
      author: 'Fyodor Dostoevsky',
    },
  ];

  const continueBooks = [
    {
      id: '1',
      cover:
        'data:image/jpeg;base64,',
      title: 'The IDIOT',
      progress: 50,
    },
    {
      id: '2',
      cover:
        'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQlIokz68gTar-Q_-91nMeg37zpcHUnEem28Q&s',
      title: 'The IDIOT',
      progress: 70,
    },
    {
      id: '3',
      cover:
        'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR4VT0AJkC-OTgLmieZrveVaXxieba20_Agjw&s',
      title: 'The Devils',
      progress: 30,
    },
    {
      id: '4',
      cover:
        'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTXpGfMYjgPsej3LKhYJsqleB6LXymjH485ng&s',
      title: 'The Brothers Karamazov',
      progress: 50,
    },
    {
      id: '5',
      cover:
        'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ-XBTX-stju3gDRz4hVhfzOOHhJf1qfx61zg&s',
      title: 'Adolescent',
      progress: 10,
    },
  ];

  const user = {
    name: 'Ixan',
    profile:
      'https://plus.unsplash.com/premium_photo-1665663927708-e3287b105c44?fm=jpg&q=60&w=3000',
  };

 const navigateToMore = useCallback((item) => {
  router.push({
    pathname: "screens/More",
    params: {param: JSON.stringify(item)}
  })
 })

  const Header = () => (
    <View
      style={{
        padding: 16,
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
      }}>
      {/* Profile + Greeting */}
      <View style={{ flexDirection: 'row', alignItems: 'center' }}>
        <Image
          source={{ uri: user.profile }}
          style={{ width: 50, height: 50, borderRadius: 30 }}
        />
        <View>
          <Text style={{ fontSize: 18, fontWeight: 'bold', marginLeft: 10 }}>
            Welcome,
          </Text>
          <Text
            style={{
              fontSize: 20,
              fontWeight: 'bold',
              marginLeft: 10,
              color: 'gold',
            }}>
            {user.name}
          </Text>
        </View>
      </View>
      <Ionicons name="search-outline" size={25} />
    </View>
  );

  const FeaturedBook = ({ book }) => {

    const {cover, title, author} = book

    return (
      <TouchableOpacity onPress={() => navigateToMore(book)}>
        <View
          style={{
            width: 150,
            margin: 8,
            borderRadius: 12,
            overflow: 'hidden',
            backgroundColor: '#f5f5f5',
          }}>
          <Image source={{ uri:cover }} style={{ width: '100%', height: 180 }} />
          <View style={{ padding: 12 }}>
            <Text>{title}</Text>
            <Text>{author}</Text>
          </View>
        </View>
      </TouchableOpacity>
    );
  };

  const FeaturedBookList = ({ data }) => (
    <View>
      <Text
        style={{
          marginHorizontal: 16,
          fontSize: 23,
          fontWeight: '800',
          marginTop: 24,
          marginBottom: 8,
        }}>
        Top Featured Books
      </Text>
      <FlatList
        data={data}
        horizontal
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => <FeaturedBook book={item} />}
        showsHorizontalScrollIndicator={false}
      />
    </View>
  );

  const ContinueList = ({ data }) => (
    <View>
      <Text
        style={{
          marginHorizontal: 16,
          fontSize: 23,
          fontWeight: '800',
          marginTop: 24,
          marginBottom: 8,
        }}>
        Continue Reading
      </Text>
      <FlatList
        data={data}
        horizontal
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => (
          <View style={{ width: 100, marginLeft: 16 }}>
            <Image
              source={{ uri: item.cover }}
              style={{ width: 100, height: 140, borderRadius: 12 }}
            />
            <Text style={{ fontSize: 12, marginTop: 4 }} numberOfLines={1}>
              {item.title}
            </Text>
            <View
              style={{
                marginTop: 4,
                height: 4,
                backgroundColor: '#eee',
                borderRadius: 2,
              }}>
              <View
                style={{
                  height: 4,
                  backgroundColor: '#6200ee',
                  borderRadius: 2,
                  width: `${item.progress}%`,
                }}
              />
            </View>
          </View>
        )}
        showsHorizontalScrollIndicator={false}
      />
    </View>
  );

  return (
    <SafeAreaView style={{ flex: 1, backgroundColor: '#fff' }}>
      <FlatList
        contentContainerStyle={{ paddingBottom: 24 }}
        ListHeaderComponent={
          <>
            <Header />
            <FeaturedBookList data={featuredBooks} />
            <ContinueList data={continueBooks} />
            <ContinueList data={continueBooks} />
            <ContinueList data={continueBooks} />
          </>
        }
      />
    </SafeAreaView>
  );
}
